mapper.insert:
insert into user (
    id, username,nickname,
    password,email
)
values(
    #{id,jdbcType=INTEGER},#{username,jdbcType=VARCHAR},#{nickname,jdbcType=VARCHAR},
    #{password,jdbcType=VARCHAR},#{email,jdbcType=VARCHAR}
)

mapper.update:
update user

    username=#{username},
    nickname=#{nickname},
    password=#{password}
    email=#{email}

where id=#{id}
